mapper.select:
SELECT 

FROM ecpp_selfevluate a

WHERE a.id = #{id}

SELECT 

FROM ecpp_selfevluate a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.attachement = #{attachement}


		AND a.status = #{status}


		AND a.shstatus = #{shstatus}


		AND a.attribute1 = #{attribute1}


		AND a.attribute2 = #{attribute2}


		AND a.attribute3 = #{attribute3}


		AND a.attribute4 = #{attribute4}


		AND a.attribute5 = #{attribute5}


		AND a.unit = #{unit.id}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}


		AND a.update_by = #{updateBy.id}


		AND a.update_date = #{updateDate}


		AND a.del_flag = #{delFlag}


		AND a.del_date = #{delDate}


		AND a.remarks = #{remarks}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ecpp_selfevluate a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ecpp_selfevluate SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ecpp_selfevluate SET
	del_flag = '1'
WHERE unit = #{unit.id}
